use 236363myDBdb.createCollection('results')var date = Date()var post={    "Res":1,    "ID1":123456789,    "ID2":123456789,    "Date":date}db.results.insert(post)db.results.find()// step 3var res = db.results.aggregate({    $unwind : "$documents"},{    $match : {Res : 2}},{    $sort : {"documents.rank" : -1, "documents.query_num" : 1}},{    $limit : 2},{   $project: { "_id" : 0 , "query_text" : "$documents.query_text" } }).result[0]res.Res = 3db.results.save(res)